var gaze = require("gaze");
var exec = require("child_process").exec;


gaze(process.cwd() + "/lib/*.js", function() {
  this.on("changed", function(file) {
    var relative = file.slice(file.lastIndexOf("lib"), file.length);
    exec("scp -r " + relative + " root@192.168.1.7:/home/root/exp/node_modules/galileo-io/" + relative, function() {

      console.log("%s Updated @ %s", relative, new Date());
    });
  });
});

